nvidia = pd.read_csv("./datasets/NVDA.csv")
intel = pd.read_csv("./datasets/INTC.csv")

combined = amd.assign(Stock="AMD").append(nvidia.assign(Stock="NVIDIA")).append(intel.assign(Stock="Intel"))

